mapper.select:
select a.id as "id", a.type as "type", a.title as "title", a.intro as "intro", a.content as "content", a.publish_user as "publish_user", a.publish_time as "publish_time", a.read_num as "read_num", a.status as "status", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time" from common_graphic a where 1=1
and a.title like concat('%',concat(#{pd.title},'%'))
and a.type = #{pd.type}
and a.status = #{pd.status}
order by a.order_by+0 asc , a.create_time desc

select count(*) from common_graphic a where 1=1
and a.title like concat('%',concat(#{pd.title},'%'))
and a.type = #{pd.type}
and a.status = #{pd.status}

select a.id as "id", a.type as "type", a.title as "title", a.intro as "intro", a.content as "content", a.publish_user as "publish_user", a.publish_time as "publish_time", a.read_num as "read_num", a.status as "status", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time" from common_graphic a where 1=1
and a.title like concat('%',concat(#{title},'%'))
and a.type = #{type}
and a.status = #{status}
order by a.order_by+0 asc , a.create_time desc
