mapper.select:
select a.table_name, a.table_comment, a.create_time, a.update_time from information_schema.tables a where a.table_schema=#{pd.table_schema} and a.table_type='base table' and a.table_name not like 'qrtz_%' and a.table_name not like 'system_%' and a.table_name not like 'act_%' and a.table_name not in ( select table_name from system_gentable )
and a.table_name like concat('%',concat(#{pd.table_name},'%'))
and a.table_comment like concat('%',concat(#{pd.table_comment},'%'))
and a.create_time >= #{pd.start_time}
and a.create_time <= #{pd.end_time}

select
count(*)
from information_schema.tables a
where a.table_schema=#{pd.table_schema}
and a.table_type='base table' and
a.table_name not like 'qrtz_%' and a.table_name not like 'system_%' and a.table_name not like 'act_%'
and a.table_name not in (
	select table_name from system_gentable
)

	and a.table_name like concat('%',concat(#{pd.table_name},'%'))


	and a.table_comment like concat('%',concat(#{pd.table_comment},'%'))


	and a.create_time >= #{pd.start_time}


	and a.create_time  <=  #{pd.end_time}

select a.id as "id", a.type as "type", a.table_name as "table_name", a.table_comment as "table_comment", a.temp_type as "temp_type", a.pack_path as "pack_path", a.mod_name as "mod_name", a.bus_name as "bus_name", a.menu_name as "menu_name", a.menu_id as "menu_id", a.gen_type as "gen_type", a.gen_path as "gen_path", a.more_add as "more_add", a.tree_id as "tree_id", a.tree_pid as "tree_pid", a.tree_name as "tree_name", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time", b.name as "menu_pname" from system_gentable a left join system_menu b on a.menu_id=b.id where 1=1
and a.table_name like concat('%',concat(#{pd.table_name},'%'))
and a.table_comment like concat('%',concat(#{pd.table_comment},'%'))
and a.create_time >= #{pd.start_time}
and a.create_time <= #{pd.end_time}
order by a.order_by+0 asc , a.create_time desc

select count(*) from system_gentable a where 1=1
and a.table_name like concat('%',concat(#{pd.table_name},'%'))
and a.table_comment like concat('%',concat(#{pd.table_comment},'%'))
and a.create_time >= #{pd.start_time}
and a.create_time <= #{pd.end_time}

select a.id as "id", a.type as "type", a.table_name as "table_name", a.table_comment as "table_comment", a.temp_type as "temp_type", a.pack_path as "pack_path", a.mod_name as "mod_name", a.bus_name as "bus_name", a.menu_name as "menu_name", a.menu_id as "menu_id", a.gen_type as "gen_type", a.gen_path as "gen_path", a.more_add as "more_add", a.tree_id as "tree_id", a.tree_pid as "tree_pid", a.tree_name as "tree_name", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time" from system_gentable a where 1=1
and a.table_name like concat('%',concat(#{table_name},'%'))
and a.table_comment like concat('%',concat(#{table_comment},'%'))
and a.create_time >= #{start_time}
and a.create_time <= #{end_time}
and a.id not in
#{id}
order by a.order_by+0 asc , a.create_time desc

select a.id as "id", a.type as "type", a.table_id as "table_id", a.field_name as "field_name", a.field_comment as "field_comment", a.field_type as "field_type", a.field_operat as "field_operat", a.field_list as "field_list", a.field_query as "field_query", a.query_type as "query_type", a.verify_rule as "verify_rule", a.show_type as "show_type", a.option_content as "option_content", a.default_value as "default_value", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time" from system_genfield a where a.table_id = #{table_id}
and a.field_name not in
#{field_name}
order by a.order_by+0 asc
