mapper.select:
select a.id as "id", a.type as "type", a.name as "name", a.short_name as "short_name", a.status as "status", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time" from system_role a where 1=1
and a.name like concat('%',concat(#{pd.name},'%'))
and a.short_name like concat('%',concat(#{pd.short_name},'%'))
and a.type = #{pd.type}
and a.status = #{pd.status}
order by a.order_by+0 asc , a.create_time desc

select count(*) from system_role a where 1=1
and a.name like concat('%',concat(#{pd.name},'%'))
and a.short_name like concat('%',concat(#{pd.short_name},'%'))
and a.type = #{pd.type}
and a.status = #{pd.status}

select a.id as "id", a.type as "type", a.name as "name", a.short_name as "short_name", a.status as "status", a.order_by as "order_by", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time" from system_role a where 1=1
and a.name like concat('%',concat(#{name},'%'))
and a.short_name like concat('%',concat(#{short_name},'%'))
and a.type = #{type}
and a.status = #{status}
order by a.order_by+0 asc , a.create_time desc

mapper.delete:
delete from system_role_menu where 1=1

	and role_id=#{role_id}


	and role_id in

		#{role_id}



	and menu_id not in

		#{id}



	and menu_id in

		#{id}
