mapper.select:
select a.id as "id", a.type as "type", a.login_name as "login_name", a.login_password as "login_password", a.name as "name", a.sex as "sex", a.phone as "phone", a.email as "email", a.birth_date as "birth_date", a.live_address as "live_address", a.birth_address as "birth_address", a.head_address as "head_address", a.motto as "motto", a.status as "status", a.order_by as "order_by", a.open_id as "open_id", a.init_password as "init_password", a.pwd_error_num as "pwd_error_num", a.pwd_error_time as "pwd_error_time", a.last_login_time as "last_login_time", a.theme_id as "theme_id", a.depart_leader as "depart_leader", a.direct_leader as "direct_leader", a.branch_leader as "branch_leader", a.remark as "remark", a.create_time as "create_time", a.create_user as "create_user", a.create_organize as "create_organize", a.update_user as "update_user", a.update_time as "update_time" from system_user a left join system_user_organize b on a.id=b.user_id where 1=1
and a.name like concat('%',concat(#{name},'%'))
and a.login_name like concat('%',concat(#{login_name},'%'))
and a.phone like concat('%',concat(#{phone},'%'))
and a.email like concat('%',concat(#{email},'%'))
and a.sex = #{sex}
and a.type = #{type}
and a.status = #{status}
and b.organize_id = #{organize_id}
order by a.order_by+0 asc , a.create_time desc

select a.id as "id", a.type as "type", a.login_name as "login_name", a.login_password as "login_password", a.name as "name", a.sex as "sex", a.phone as "phone", a.email as "email" from system_user a where 1=1
and a.login_name in
#{login_name}
and a.phone in
#{phone}
order by a.order_by+0 asc , a.create_time desc

select a.id as "id", a.user_id as "user_id", a.organize_id as "organize_id", a.type as "type", a.position as "position", a.authOrgIds as "authOrgIds", a.authParams as "authParams", a.authOrgCascade as "authOrgCascade", a.use_status as "use_status", a.order_by as "order_by", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time", b.name as "organize_name" from system_user_organize a left join system_organize b on a.organize_id = b.id where 1=1
and a.user_id = #{user_id}
and a.organize_id = #{organize_id}
order by a.type+0 asc, a.order_by+0 asc , a.create_time desc

select a.id as "id", a.user_id as "user_id", a.organize_id as "organize_id", a.type as "type", a.position as "position", a.authOrgIds as "authOrgIds", a.authParams as "authParams", a.authOrgCascade as "authOrgCascade", a.use_status as "use_status", a.order_by as "order_by", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time", b.name as "organize_name", b.org_cascade as "org_cascade" from system_user_organize a left join system_organize b on a.organize_id = b.id where 1=1
and a.user_id = #{user_id}
and a.organize_id = #{organize_id}
order by a.use_status+0 asc,a.order_by+0 asc,a.type+0 asc limit 1

mapper.delete:
delete from system_user_organize where 1=1

	and id = #{id}


	and user_id in

		#{user_id}



	and organize_id in
	(
		select a.id from (
		select id from system_organize
			where org_cascade like
			concat('%',concat((select a.org_cascade from (select id,org_cascade from system_organize) a where a.id
			in (
			#{id}
			) ),'%'))

		) a
	)

mapper.update:
update system_user_organize set
authOrgIds=#{authOrgIds},
authParams=#{authParams},
authOrgCascade=#{authOrgCascade},
update_time=#{update_time}
where user_id = #{user_id} and organize_id = #{organize_id}

mapper.select:
select a.id as "id", a.type as "type", a.name as "name", a.short_name as "short_name", a.status as "status" from system_role a left join system_user_role b on a.id=b.role_id where b.user_id=#{user_id} and a.status='0' union all select a.id as "id", a.type as "type", a.name as "name", a.short_name as "short_name", a.status as "status" from system_role a left join system_organize_role b on a.id=b.role_id where a.status='0' and b.organize_id in ( select organize_id from system_user_organize where user_id=#{user_id}
and organize_id = #{organize_id}
)

mapper.delete:
delete from system_user_role where 1=1

	and user_id = #{user_id}


	and user_id in

		#{user_id}



	and role_id in

		#{role_id}

delete from system_user_group where 1=1

	and group_id in

		#{group_id}



	and user_id in

		#{user_id}

mapper.update:
update system_user_organize set
use_status=#{use_status},
update_time=#{update_time}
where 1=1

	and user_id = #{user_id}


	and organize_id = #{organize_id}

mapper.select:
select a.id as "id", a.user_id as "user_id", a.organize_id as "organize_id", a.type as "type", a.position as "position", a.authOrgIds as "authOrgIds", a.authParams as "authParams", a.authOrgCascade as "authOrgCascade", a.use_status as "use_status", a.order_by as "order_by", a.create_user as "create_user", a.create_time as "create_time", a.update_time as "update_time", b.name as "organize_name" from system_user_organize a left join system_organize b on a.organize_id = b.id where 1=1
and a.user_id = #{pd.user_id}
and a.organize_id = #{pd.organize_id}
order by a.type+0 asc, a.order_by+0 asc , a.create_time desc

select
count(*)
from system_user_organize a
where 1=1

	and a.user_id = #{pd.user_id}


	and a.organize_id = #{pd.organize_id}

mapper.update:
update system_user set
token = '',
token_refresh_time=#{token_refresh_time},
update_time=#{update_time}
where token=#{token}
